import React from 'react';
import { useTrack } from '../context/TrackContext';

const TrackList = React.memo(function TrackList({ filteredTracks }) {
  const { selectedTrack, setSelectedTrack } = useTrack();
  return (
    <div className="tracks-section">
      <div className="tracks-grid">
        {filteredTracks.map((track, index) => (
          
          <div
            key={track._id}
            className={`track-card ${selectedTrack?._id === track._id ? 'playing' : ''}`}
            onClick={() => setSelectedTrack(track)}
            style={{ cursor: 'pointer' }}
          >
            <div className="track-cover-container">
              <img
                src={`http://localhost:5000/${track.coverImage}`}
                alt={track.title}
                className="track-cover"
              />
            </div>
            <div className="track-info">
              <h3>{track.title}</h3>
              <p>{track.artist?.name || 'Unknown Artist'}</p>
              <p className="genre">{track.genre}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
});

export default TrackList;
