import React, { useState } from 'react';
import axios from 'axios';

const TrackCard = React.memo(function TrackCard({ track, isSelected, onSelect }) {
  const [liked, setLiked] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleLike = async (e) => {
    e.stopPropagation(); // Prevent triggering play/select
    setLoading(true);
    console.log('Like button clicked for trackId:', track._id);
    try {
      const token = localStorage.getItem('token');
      if (!liked) {
        await axios.post('http://localhost:5000/api/like/like', { trackId: track._id }, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setLiked(true);
      } else {
        await axios.post('http://localhost:5000/api/like/unlike', { trackId: track._id }, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setLiked(false);
      }
    } catch (err) {
      console.error('Error updating like status:', err);
      alert(err.response?.data?.message || 'Error updating like status.');
    } finally {
      setLoading(false);
    }
  };

  console.log('Rendering track card:', track.title, 'Selected:', isSelected);
  return (
    <div
      className={`track-card ${isSelected ? 'playing' : ''}`}
      onClick={() => onSelect(track)}
      style={{ cursor: 'pointer' }}
    >
      <div className="track-cover-container" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <img
          src={`http://localhost:5000/${track.coverImage}`}
          alt={track.title}
          className="track-cover"
        />
        <button
          className="like-button"
          onClick={handleLike}
          disabled={loading}
          aria-label={liked ? 'Unlike' : 'Like'}
          style={{
            background: 'none',
            border: 'none',
            outline: 'none',
            cursor: 'pointer',
            marginTop: '0.3em',
            borderRadius: '50%',
            padding: '0.3em 0.5em',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '2em',
            color: liked ? 'red' : '#aaa',
            transition: 'color 0.2s',
            zIndex: 10,
          }}
        >
          {liked ? '♥' : '♡'}
        </button>
      </div>
      <div className="track-info">
        <h3>{track.title}</h3>
        <p>{track.artist?.name || 'Unknown Artist'}</p>
        <p className="genre">{track.genre}</p>
      </div>
    </div>
  );
});

export default TrackCard;
