/**
 * AudioPlayer component
 * ---------------------
 * Extracted from BrowsePage to optimize rendering.
 * Handles all audio playback logic and state locally.
 * Memoized with React.memo to prevent unnecessary re-renders.
 * [Optimization] Now uses the custom player UI for consistent styling.
 */
import React, { useRef, useState, useEffect, useCallback } from 'react';

const AudioPlayer = React.memo(function AudioPlayer({
  track,
  isPlaying,
  onPlayPause,
  onEnded,
  initialVolume = 1
}) {
  const audioRef = useRef(null);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(initialVolume);

  // Play/pause effect
  useEffect(() => {
    if (!audioRef.current) return;
    if (isPlaying) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  }, [isPlaying, track]);

  // Update duration on metadata load
  const handleLoadedMetadata = useCallback(() => {
    setDuration(audioRef.current.duration);
  }, []);

  // Update current time on time update
  const handleTimeUpdate = useCallback(() => {
    setCurrentTime(audioRef.current.currentTime);
  }, []);

  // Seek handler
  const handleSeek = useCallback((e) => {
    const time = Number(e.target.value);
    audioRef.current.currentTime = time;
    setCurrentTime(time);
  }, []);

  // Volume handler
  const handleVolumeChange = useCallback((e) => {
    const vol = Number(e.target.value);
    setVolume(vol);
    audioRef.current.volume = vol;
  }, []);

  // Ended handler
  const handleEnded = useCallback(() => {
    if (onEnded) onEnded();
  }, [onEnded]);

  // Format time helper
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  if (!track) return null;

  return (
    <div className="music-player">
      <div className="player-info">
        <div className="track-cover-container">
          <img
            src={track.coverImage ? `http://localhost:5000/${track.coverImage}` : ''}
            alt={track.title}
            className="track-cover"
          />
        </div>
        <div className="track-details">
          <h3>{track.title}</h3>
          <p>{track.artist?.name || 'Unknown Artist'}</p>
        </div>
      </div>
      <div className="volume-controls">
        <span className="volume-icon">🔊</span>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
          className="volume-slider"
        />
      </div>
      <div className="player-controls">
        <div className="control-buttons">
          {/* You can add previous/next logic if needed */}
          <button className="control-button play-pause" onClick={onPlayPause}>
            {isPlaying ? '❚❚' : '▶'}
          </button>
        </div>
        <div className="progress-bar">
          <span className="time-info">{formatTime(currentTime)}</span>
          <input
            type="range"
            min="0"
            max={duration || 0}
            value={currentTime}
            onChange={handleSeek}
            className="progress-slider"
          />
          <span className="time-info">{formatTime(duration)}</span>
        </div>
      </div>
      <audio
        ref={audioRef}
        src={track.audioUrl}
        onLoadedMetadata={handleLoadedMetadata}
        onTimeUpdate={handleTimeUpdate}
        onEnded={handleEnded}
      />
    </div>
  );
});

export default AudioPlayer;
