import React from 'react';

const TrackCard = React.memo(function TrackCard({ track, isSelected, onSelect }) {
  console.log('Rendering track card:', track.title, 'Selected:', isSelected);
  return (
    <div
      className={`track-card ${isSelected ? 'playing' : ''}`}
      onClick={() => onSelect(track)}
      style={{ cursor: 'pointer' }}
    >
      <div className="track-cover-container">
        <img
          src={`http://localhost:5000/${track.coverImage}`}
          alt={track.title}
          className="track-cover"
        />
      </div>
      <div className="track-info">
        <h3>{track.title}</h3>
        <p>{track.artist?.name || 'Unknown Artist'}</p>
        <p className="genre">{track.genre}</p>
        <button className="like-button">Like</button>
      </div>
    </div>
  );
});

export default TrackCard;
