import React, { useRef, useEffect } from 'react';

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',
  background: 'rgba(0,0,0,0.6)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  zIndex: 2000,
};

const modalStyle = {
  background: '#222',
  borderRadius: '12px',
  padding: '2rem',
  minWidth: '350px',
  maxWidth: '90vw',
  boxShadow: '0 4px 32px rgba(0,0,0,0.3)',
  color: 'white',
  position: 'relative',
};

function CommentOverlay({ open, onClose, track }) {
  const modalRef = useRef();

  useEffect(() => {
    function handleKeyDown(e) {
      if (e.key === 'Escape') onClose();
    }
    if (open) {
      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [open, onClose]);

  if (!open) return null;

  function handleOverlayClick(e) {
    if (modalRef.current && !modalRef.current.contains(e.target)) {
      onClose();
    }
  }

  return (
    <div style={overlayStyle} onMouseDown={handleOverlayClick}>
      <div style={modalStyle} ref={modalRef} onMouseDown={e => e.stopPropagation()}>
        <h2>Add Comment</h2>
        <p>Track: <b>{track?.title}</b></p>
        <textarea
          placeholder="Write your comment..."
          style={{ width: '100%', minHeight: '80px', borderRadius: '6px', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #444', background: '#181818', color: 'white' }}
        />
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem' }}>
          <button onClick={onClose} style={{ background: '#444', color: 'white', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', cursor: 'pointer' }}>Close</button>
          <button style={{ background: '#1db954', color: 'white', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', cursor: 'pointer' }}>Comment</button>
        </div>
      </div>
    </div>
  );
}

export default CommentOverlay;
