import React, { createContext, useContext, useState } from 'react';

const TrackSelectionContext = createContext();

export function TrackSelectionProvider({ children }) {
  const [selectedTrackId, setSelectedTrackId] = useState(null);
  return (
    <TrackSelectionContext.Provider value={{ selectedTrackId, setSelectedTrackId }}>
      {children}
    </TrackSelectionContext.Provider>
  );
}

export function useTrackSelection() {
  return useContext(TrackSelectionContext);
}
