import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useSubscription } from '../context/SubscriptionContext';
import { useAuth } from '../context/AuthContext';
import { useSearchFilter } from '../context/SearchFilterContext';
import '../styles/BrowsePage.css';
import AudioPlayer from '../components/AudioPlayer';
import TrackList from '../components/TrackList';

const BrowsePage = () => {
  console.log('renderingbrowsepage');

  const { hasSubscription, createCheckoutSession, loading: subscriptionLoading, subscriptionDetails } = useSubscription();
  const { isAuthenticated } = useAuth();
  const { searchQuery, selectedGenre, selectedArtist } = useSearchFilter();
  const navigate = useNavigate();
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);
  const limit = 20;

  const fetchTracks = async (pageNum = 1, q, genre, artist) => {
    setLoading(true);
    try {
      const params = {
        page: pageNum,
        limit,
      };
      if (q) params.q = q;
      if (genre) params.genre = genre;
      if (artist) params.artist = artist;
      const response = await axios.get('http://localhost:5000/api/publicTracks/approved', { params });
      setTracks(response.data.tracks);
      setTotalPages(response.data.totalPages);
      setTotal(response.data.total);
      setLoading(false);
    } catch (err) {
      setError('Failed to load tracks. Please try again later.');
      setLoading(false);
    }
  };

  // Fetch tracks when page or filters/search change
  useEffect(() => {
    fetchTracks(page, searchQuery, selectedGenre, selectedArtist);
    // eslint-disable-next-line
  }, [page, searchQuery, selectedGenre, selectedArtist]);

  // Reset to page 1 when filters/search change
  useEffect(() => {
    setPage(1);
    // eslint-disable-next-line
  }, [searchQuery, selectedGenre, selectedArtist]);

  const handleSubscribeClick = () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    createCheckoutSession();
  };

  if (loading || subscriptionLoading) return <div>Loading...</div>;
  if (error) return <div className="error">{error}</div>;
  // console.log('renderbrowsepage');

  return (
    <div className="browse-page">
      <div className="browse-header">
        {isAuthenticated && !hasSubscription && (
          <button 
            className="subscribe-button"
            onClick={handleSubscribeClick}
          >
            Subscribe to Stream Music - $5/month
          </button>
        )}
        {!isAuthenticated && (
          <div className="auth-prompt">
            <p>Please <button onClick={() => navigate('/login')} className="auth-link">login</button> or <button onClick={() => navigate('/register')} className="auth-link">sign up</button> to subscribe and stream music</p>
          </div>
        )}
      </div>
      <TrackList
        tracks={tracks}
        hasSubscription={hasSubscription}
        subscriptionDetails={subscriptionDetails}
      />
      {/* Pagination Controls */}
      <div className="pagination-controls" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', margin: '1.5rem 0' }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1} style={{ marginRight: '1rem' }}>Previous</button>
        <span>Page {page} of {totalPages} ({total} tracks)</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages} style={{ marginLeft: '1rem' }}>Next</button>
      </div>
      <AudioPlayer />
    </div>
  );
};

export default BrowsePage;
