import React from 'react';
import { useTrack } from '../context/TrackContext';
import TrackList from './TrackList';
import TrackCard from './TrackCard';

export default function TrackListWithSelection(props) {
  const { selectedTrack, setSelectedTrack } = useTrack();
  const { tracks, ...rest } = props;

  // Filtering logic can be moved here if needed
  const trackCards = tracks.map(track => (
    <TrackCard
      key={track._id}
      track={track}
      isSelected={selectedTrack?._id === track._id}
      onSelect={setSelectedTrack}
      onOpenComments={props.onOpenComments}
    />
  ));

  return (
    <TrackList {...rest} tracks={tracks}>
      {trackCards}
    </TrackList>
  );
}
