import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useSubscriptionField } from '../context/SubscriptionContext';
import { useAuth } from '../context/AuthContext';
import { useCommentOverlay } from '../context/CommentOverlayContext';
import '../styles/BrowsePage.css';
import AudioPlayer from '../components/AudioPlayer';
import TrackList from '../components/TrackList';
import CommentOverlay from '../components/CommentOverlay';

const BrowsePage = () => {
  console.log('rendering browsepage');

  // Use field-level selectors for subscription context
  const hasSubscription = useSubscriptionField(ctx => ctx.hasSubscription);
  const createCheckoutSession = useSubscriptionField(ctx => ctx.createCheckoutSession);
  const subscriptionLoading = useSubscriptionField(ctx => ctx.loading);
  const subscriptionDetails = useSubscriptionField(ctx => ctx.subscriptionDetails);

  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);
  const limit = 20;

  // Use the comment overlay context
  const { open, track, allTracks, openOverlay, closeOverlay } = useCommentOverlay();

  const fetchTracks = async (pageNum = 1) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/publicTracks/approved?page=${pageNum}&limit=${limit}`);
      setTracks(response.data.tracks);
      setTotalPages(response.data.totalPages);
      setTotal(response.data.total);
      setLoading(false);
    } catch (err) {
      setError('Failed to load tracks. Please try again later.');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTracks(page);
    // eslint-disable-next-line
  }, [page]);

  const handleSubscribeClick = () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    createCheckoutSession();
  };

  if (loading || subscriptionLoading) return <div>Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="browse-page">
      <div className="browse-header">
        {isAuthenticated && !hasSubscription && (
          <button 
            className="subscribe-button"
            onClick={handleSubscribeClick}
          >
            Subscribe to Stream Music - $5/month
          </button>
        )}
        {!isAuthenticated && (
          <div className="auth-prompt">
            <p>Please <button onClick={() => navigate('/login')} className="auth-link">login</button> or <button onClick={() => navigate('/register')} className="auth-link">sign up</button> to subscribe and stream music</p>
          </div>
        )}
      </div>
      <TrackList
        tracks={tracks}
        hasSubscription={hasSubscription}
        subscriptionDetails={subscriptionDetails}
        onOpenComments={(track) => openOverlay(track, tracks)}
      />
      {/* Pagination Controls */}
      <div className="pagination-controls" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', margin: '1.5rem 0' }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1} style={{ marginRight: '1rem' }}>Previous</button>
        <span>Page {page} of {totalPages} ({total} tracks)</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages} style={{ marginLeft: '1rem' }}>Next</button>
      </div>
      <AudioPlayer />
      <CommentOverlay
        open={open}
        onClose={closeOverlay}
        track={track}
        allTracks={allTracks}
      />
    </div>
  );
};

export default BrowsePage;
