import React, { createContext, useContext, useState, useCallback } from 'react';

const CommentOverlayContext = createContext();

export const useCommentOverlay = () => useContext(CommentOverlayContext);

export const CommentOverlayProvider = ({ children }) => {
  const [open, setOpen] = useState(false);
  const [track, setTrack] = useState(null);
  const [allTracks, setAllTracks] = useState([]);

  const openOverlay = useCallback((trackObj, tracksArr = []) => {
    setTrack(trackObj);
    setAllTracks(tracksArr);
    setOpen(true);
  }, []);

  const closeOverlay = useCallback(() => {
    setOpen(false);
    setTrack(null);
    setAllTracks([]);
  }, []);

  return (
    <CommentOverlayContext.Provider value={{
      open,
      track,
      allTracks,
      openOverlay,
      closeOverlay
    }}>
      {children}
    </CommentOverlayContext.Provider>
  );
};
