import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { loadStripe } from '@stripe/stripe-js';
import { useAuth } from './AuthContext';
import { createContext, useContextSelector } from 'use-context-selector';

// Create the context using use-context-selector
const SubscriptionContext = createContext(null);

// Custom hook to select a field from the subscription context
export const useSubscriptionField = (selector) => useContextSelector(SubscriptionContext, selector);

// Provider component
export const SubscriptionProvider = ({ children }) => {
  const [hasSubscription, setHasSubscription] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useAuth();
  const [subscriptionDetails, setSubscriptionDetails] = useState(null);

  // Check subscription status when auth state changes
  useEffect(() => {
    checkSubscriptionStatus();
  }, [isAuthenticated]);

  const checkSubscriptionStatus = async () => {
    if (!isAuthenticated) {
      setHasSubscription(false);
      setLoading(false);
      setSubscriptionDetails(null);
      return;
    }
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setHasSubscription(false);
        setLoading(false);
        setSubscriptionDetails(null);
        return;
      }
      const response = await axios.get('http://localhost:5000/api/subscription/status', {
        headers: { Authorization: `Bearer ${token}` }
      });
      const hasAccess = response.data.hasSubscription;
      const isCancelled = response.data.status === 'cancelled';
      const currentPeriodEnd = new Date(response.data.currentPeriodEnd);
      const now = new Date();
      const shouldHaveAccess = hasAccess && (!isCancelled || (isCancelled && currentPeriodEnd > now));
      setHasSubscription(shouldHaveAccess);
      setSubscriptionDetails({
        ...response.data,
        hasSubscription: shouldHaveAccess
      });
      setLoading(false);
    } catch (err) {
      setError('Failed to check subscription status');
      setLoading(false);
      setHasSubscription(false);
      setSubscriptionDetails(null);
    }
  };

  const createCheckoutSession = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Not authenticated');
      }
      const response = await axios.post(
        'http://localhost:5000/api/subscription/create-checkout-session',
        {},
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      const stripe = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
      await stripe.redirectToCheckout({
        sessionId: response.data.sessionId
      });
    } catch (err) {
      setError('Failed to create checkout session');
      throw err;
    }
  };

  const cancelSubscription = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Not authenticated');
      }
      const response = await axios.post(
        'http://localhost:5000/api/subscription/cancel',
        {},
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      await checkSubscriptionStatus();
      return { success: true, message: response.data.message };
    } catch (error) {
      checkSubscriptionStatus();
      return {
        success: false,
        message: error.response?.data?.message || 'Failed to cancel subscription'
      };
    }
  };

  // Memoize the context value for use-context-selector
  const value = useMemo(() => ({
    hasSubscription,
    loading,
    error,
    createCheckoutSession,
    checkSubscriptionStatus,
    cancelSubscription,
    subscriptionDetails
  }), [hasSubscription, loading, error, createCheckoutSession, checkSubscriptionStatus, cancelSubscription, subscriptionDetails]);

  return (
    <SubscriptionContext.Provider value={value}>
      {children}
    </SubscriptionContext.Provider>
  );
};

export default SubscriptionContext;
