import React, { useState, useMemo } from 'react';
import { createContext, useContextSelector } from 'use-context-selector';

const TrackContext = createContext();

export function TrackProvider({ children }) {
  const [selectedTrack, setSelectedTrack] = useState(null);
  const value = useMemo(() => ({ selectedTrack, setSelectedTrack }), [selectedTrack]);
  return <TrackContext.Provider value={value}>{children}</TrackContext.Provider>;
}

export function useSelectedTrackId() {
  return useContextSelector(TrackContext, v => v.selectedTrack?._id);
}

export function useSetSelectedTrack() {
  return useContextSelector(TrackContext, v => v.setSelectedTrack);
}
