import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useSearchFilter } from '../context/SearchFilterContext';
import { useDebounce } from '../hooks/useDebounce';

const NavbarSearch = React.memo(() => {
  const navigate = useNavigate();
  const location = useLocation();
  const { searchQuery, setSearchQuery } = useSearchFilter();
  const [searchTerm, setSearchTerm] = useState('');
  const debouncedSearchTerm = useDebounce(searchTerm, 400);

  // Initialize search term from URL query parameter
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const query = params.get('q');
    if (query) {
      setSearchTerm(query);
      setSearchQuery(query);
    }
  }, [location.search, setSearchQuery]);

  // Debounced effect for search
  useEffect(() => {
    if (debouncedSearchTerm.trim() !== searchQuery) {
      setSearchQuery(debouncedSearchTerm.trim());
      if (debouncedSearchTerm.trim()) {
        navigate(`/browse?q=${encodeURIComponent(debouncedSearchTerm.trim())}`);
      }
    }
    // eslint-disable-next-line
  }, [debouncedSearchTerm]);

  const handleSearch = () => {
    if (searchTerm.trim()) {
      setSearchQuery(searchTerm.trim());
      navigate(`/browse?q=${encodeURIComponent(searchTerm.trim())}`);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
  };

  return (
    <div className="search-bar navbar-search">
      <input
        type="text"
        placeholder="Search..."
        className="search-input"
        value={searchTerm}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
      />
      <button className="search-button" onClick={handleSearch}>
        Search
      </button>
    </div>
  );
});

export default NavbarSearch;
