import React, { useRef, useEffect } from 'react';

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',
  background: 'rgba(0,0,0,0.3)',
  zIndex: 2000,
  display: 'flex',
  justifyContent: 'flex-end',
};

const drawerStyle = {
  width: 'min(400px, 100vw)',
  maxWidth: '100vw',
  height: '100vh',
  background: '#181818',
  boxShadow: '-2px 0 16px rgba(0,0,0,0.2)',
  display: 'flex',
  flexDirection: 'column',
  position: 'relative',
  animation: 'slideInRight 0.2s',
};

const headerStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '1rem',
  borderBottom: '1px solid #222',
  background: '#222',
};

const commentsListStyle = {
  flex: 1,
  overflowY: 'auto',
  padding: '1rem',
  color: 'white',
};

const inputSectionStyle = {
  borderTop: '1px solid #222',
  padding: '1rem',
  background: '#222',
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem',
};

const closeBtnStyle = {
  background: 'none',
  border: 'none',
  color: '#fff',
  fontSize: '1.5rem',
  cursor: 'pointer',
};

function CommentOverlay({ open, onClose, track }) {
  const drawerRef = useRef();

  useEffect(() => {
    function handleKeyDown(e) {
      if (e.key === 'Escape') onClose();
    }
    if (open) {
      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [open, onClose]);

  if (!open) return null;

  function handleOverlayClick(e) {
    if (drawerRef.current && !drawerRef.current.contains(e.target)) {
      onClose();
    }
  }

  // Placeholder comments
  const comments = [
    { id: 1, user: 'Alice', text: 'Love this track!' },
    { id: 2, user: 'Bob', text: 'Amazing beat.' },
    { id: 3, user: 'Charlie', text: 'On repeat! ðŸ”¥' },
  ];

  return (
    <div style={overlayStyle} onMouseDown={handleOverlayClick}>
      <div style={drawerStyle} ref={drawerRef} onMouseDown={e => e.stopPropagation()}>
        <div style={headerStyle}>
          <span><b>Comments</b> â€” {track?.title}</span>
          <button onClick={onClose} style={closeBtnStyle} aria-label="Close">Ã—</button>
        </div>
        <div style={commentsListStyle}>
          {comments.length === 0 ? (
            <p style={{ color: '#aaa' }}>No comments yet. Be the first to comment!</p>
          ) : (
            comments.map(c => (
              <div key={c.id} style={{ marginBottom: '1.2rem' }}>
                <b style={{ color: '#1db954' }}>{c.user}</b>
                <div style={{ marginTop: '0.2rem', color: '#fff' }}>{c.text}</div>
              </div>
            ))
          )}
        </div>
        <div style={inputSectionStyle}>
          <textarea
            placeholder="Write your comment..."
            style={{ width: '100%', minHeight: '60px', borderRadius: '6px', padding: '0.5rem', border: '1px solid #444', background: '#181818', color: 'white', resize: 'vertical' }}
          />
          <button style={{ background: '#1db954', color: 'white', border: 'none', borderRadius: '6px', padding: '0.5rem 1.2rem', cursor: 'pointer', alignSelf: 'flex-end' }}>Comment</button>
        </div>
      </div>
      <style>{`
        @keyframes slideInRight {
          from { transform: translateX(100%); }
          to { transform: translateX(0); }
        }
      `}</style>
    </div>
  );
}

export default CommentOverlay;
