import React, { createContext, useContext, useState } from 'react';

// State context: holds selectedTrack only
const TrackStateContext = createContext();
// Actions context: holds setSelectedTrack only
const TrackActionsContext = createContext();

export function TrackProvider({ children }) {
  const [selectedTrack, setSelectedTrack] = useState(null);

  return (
    <TrackStateContext.Provider value={selectedTrack}>
      <TrackActionsContext.Provider value={setSelectedTrack}>
        {children}
      </TrackActionsContext.Provider>
    </TrackStateContext.Provider>
  );
}

// Hook to get selectedTrack only
export function useSelectedTrack() {
  return useContext(TrackStateContext);
}
// Hook to get setSelectedTrack only
export function useTrackActions() {
  return useContext(TrackActionsContext);
}
// Deprecated: legacy hook for backward compatibility
export function useTrack() {
  const selectedTrack = useContext(TrackStateContext);
  const setSelectedTrack = useContext(TrackActionsContext);
  return { selectedTrack, setSelectedTrack };
}
